package d0322;

public class ggd22 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("구구단");
		for (int j=1; j<=9;j++){
			for(int i=2; i<=9; i++) 
			{
				System.out.print(i+"*"+j+"="+i*j+"\t");
			}
			System.out.println("");
	}
	}
}
